package com.lms.tenant.persistence.entity; import jakarta.persistence.*; import java.time.Instant; import java.util.UUID; @Entity @Table(name="tenant_overage") public class TenantOverage{ @Id @Column(name="overage_id") private UUID id; @Column(name="tenant_id") private UUID tenantId; @Column(name="feature_key") private String featureKey; @Column(name="quantity") private long quantity; @Column(name="unit_price_minor") private long unitPriceMinor; @Column(name="currency") private String currency; @Column(name="occurred_at") private Instant occurredAt; @Column(name="period_start") private Instant periodStart; @Column(name="period_end") private Instant periodEnd; @Enumerated(EnumType.STRING) @Column(name="status") private OverageStatus status; @Column(name="idempotency_key") private String idempotencyKey; @Column(name="metadata", columnDefinition="jsonb") private String metadataJson; public TenantOverage(){} }